Analytics.Script:
CREATE TEMPORARY TABLE DevicesonarData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_IOT_DEVICES_sonar");
CREATE TEMPORARY TABLE DevicesonarSummaryData USING CarbonAnalytics OPTIONS (tableName "DEVICE_sonar_SUMMARY", schema "sonar FLOAT, deviceType STRING -i, deviceId STRING -i, owner STRING -i, time LONG -i",primaryKeys "deviceType, deviceId, owner, time");
insert overwrite table DevicesonarSummaryData select sonar, meta_deviceType as deviceType, meta_deviceId as deviceId, meta_owner as owner, cast(meta_time/1000 as BIGINT)as time from DevicesonarData group by sonar, meta_deviceType, meta_deviceId, meta_owner, cast(meta_time/1000 as BIGINT);
